import React, { useEffect, useState } from "react";
import { fetchReviews } from "../firebaseReviews";
import { Loader, AlertCircle, MessageSquare } from "lucide-react";

const ReviewsPage = () => {
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getReviews = async () => {
      setLoading(true);
      try {
        const allReviews = await fetchReviews();
        setReviews(allReviews);
      } catch (err) {
        setError("Failed to fetch reviews. Please try again later.");
      } finally {
        setLoading(false);
      }
    };
    getReviews();
  }, []);

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 text-gray-900 dark:text-gray-100 p-4 sm:p-6 lg:p-8">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-3xl sm:text-4xl font-bold tracking-tight mb-6">All Product Reviews</h1>
        {loading ? (
          <div className="flex justify-center items-center h-64">
            <Loader className="animate-spin text-green-500" size={48} />
          </div>
        ) : error ? (
          <div className="flex flex-col items-center justify-center h-64 bg-red-50 dark:bg-red-900/20 text-red-700 dark:text-red-300 rounded-lg p-6">
            <AlertCircle size={48} className="mb-4" />
            <h3 className="text-xl font-semibold">An Error Occurred</h3>
            <p className="mt-1">{error}</p>
          </div>
        ) : reviews.length === 0 ? (
          <div className="flex flex-col items-center justify-center h-64 bg-gray-100 dark:bg-gray-800 rounded-lg p-6">
            <MessageSquare size={48} className="mb-4 text-gray-400" />
            <h3 className="text-xl font-semibold">No Reviews Found</h3>
            <p className="mt-1 text-gray-500">There are no reviews yet.</p>
          </div>
        ) : (
          <ul className="divide-y divide-gray-200 dark:divide-gray-700">
            {reviews.map((review) => (
              <li key={review.id} className="py-4">
                <div className="flex flex-col">
                  <span className="font-semibold text-green-700 dark:text-green-400">{review.productName || "Product"}</span>
                  <span className="text-gray-700 dark:text-gray-300 mt-1">{review.text}</span>
                  <span className="text-xs text-gray-500 mt-2">By User: {review.userId} â€¢ {review.timestamp ? new Date(review.timestamp.seconds ? review.timestamp.seconds * 1000 : review.timestamp).toLocaleString() : ""}</span>
                </div>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default ReviewsPage;
