import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Header = ({ isAdmin, user, onLogin, onLogout, onHomeClick }) => {
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const userPhoto = user && user.photoURL;
  const userName = user && (user.displayName || user.email);

  // Close dropdowns on outside click
  React.useEffect(() => {
    function handleClick(e) {
      // Hamburger menu
      if (menuOpen && !document.getElementById('main-nav-menu').contains(e.target) && !document.getElementById('hamburger-btn').contains(e.target)) {
        setMenuOpen(false);
      }
      // Profile dropdown
      if (dropdownOpen && !document.getElementById('profile-dropdown')?.contains(e.target) && !document.getElementById('profile-btn')?.contains(e.target)) {
        setDropdownOpen(false);
      }
    }
    if (menuOpen || dropdownOpen) {
      document.addEventListener('mousedown', handleClick);
    }
    return () => document.removeEventListener('mousedown', handleClick);
  }, [menuOpen, dropdownOpen]);

  return (
    <header className="bg-white shadow sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-2 sm:px-4 py-3 sm:py-4 w-full">
        <div className="flex flex-col md:flex-row w-full md:justify-around md:items-center items-center relative">
          {/* Logo left */}
          {/* Logo only visible between 426px and md (tablet) */}
          <button
            className="text-2xl font-bold text-green-700 bg-transparent border-none outline-none cursor-pointer hover:underline p-0 m-0 md:hidden max-[425px]:hidden"
            style={{ fontFamily: 'inherit' }}
            onClick={onHomeClick}
          >
            Reaper Resins
          </button>
          {/* Nav center */}
          <nav
            id="main-nav-menu"
            className={`
              flex flex-col w-full mt-4 space-y-4
              sm:flex sm:flex-row sm:items-center sm:w-auto sm:mt-0 sm:space-y-0 sm:space-x-6
              max-[425px]:hidden
            `}
          >
            <a href="#products" className="block text-lg text-gray-700 hover:text-green-700 transition px-3 py-3 rounded-lg sm:px-0 sm:py-0">Products</a>
            <a href="#reviews" className="block text-lg text-gray-700 hover:text-green-700 transition px-3 py-3 rounded-lg sm:px-0 sm:py-0">Reviews</a>
            <a href="#contact" className="block text-lg text-gray-700 hover:text-green-700 transition px-3 py-3 rounded-lg sm:px-0 sm:py-0">Contact</a>
          </nav>
          {/* Nav/profile/login right */}
          <div className="flex w-full sm:w-auto justify-end items-center mt-2 sm:mt-0 gap-2">
            {user ? (
              <button
                id="profile-btn"
                className="flex items-center gap-2 focus:outline-none ml-4 px-2 py-1 rounded-full hover:bg-green-50 transition max-[425px]:flex"
                onClick={() => setDropdownOpen((o) => !o)}
                aria-label="User menu"
              >
                <img
                  src={userPhoto || `https://ui-avatars.com/api/?name=${encodeURIComponent(userName || 'U')}`}
                  alt="User avatar"
                  className="w-8 h-8 rounded-full border-2 border-green-700 shadow-sm"
                />
                <span className="text-green-900 font-medium text-base max-w-[120px] truncate max-[425px]:hidden">{userName}</span>
                <span className="text-xs text-gray-500 ml-1 max-[425px]:hidden">â–¼</span>
              </button>
            ) : (
              <button onClick={onLogin} className="w-full sm:w-auto mt-2 sm:mt-0 bg-green-700 text-white px-5 py-3 rounded-lg text-lg hover:bg-green-800 transition max-[425px]:hidden">Login</button>
            )}
            {/* Hamburger for mobile */}
            <button
              id="hamburger-btn"
              className="sm:hidden text-3xl text-green-700 focus:outline-none ml-2"
              aria-label={menuOpen ? "Close navigation menu" : "Open navigation menu"}
              onClick={() => setMenuOpen(m => !m)}
            >
              {menuOpen ? <span>&times;</span> : <span>&#9776;</span>}
            </button>
            {/* Profile dropdown (desktop & mobile) */}
            {user && dropdownOpen && (
              <div
                id="profile-dropdown"
                className={`absolute right-2 top-20 mt-2 w-56 bg-white rounded-lg shadow-lg py-2 z-50 border`}
                style={{ minWidth: 220 }}
              >
                <button
                  className="absolute top-2 right-2 text-gray-400 hover:text-red-500 text-xl font-bold focus:outline-none"
                  onClick={() => setDropdownOpen(false)}
                  aria-label="Close dropdown"
                >
                  &times;
                </button>
                <div className="px-4 py-2 text-gray-800 font-semibold border-b">{userName}</div>
                <button className="block w-full text-left px-4 py-2 hover:bg-green-50 text-gray-700" onClick={() => { setDropdownOpen(false); setMenuOpen(false); navigate('/profile'); }}>Profile</button>
                <button className="block w-full text-left px-4 py-2 hover:bg-green-50 text-gray-700" onClick={() => { setDropdownOpen(false); setMenuOpen(false); navigate('/profile/settings'); }}>Profile Settings</button>
                <button className="block w-full text-left px-4 py-2 hover:bg-green-50 text-gray-700" onClick={() => { setDropdownOpen(false); setMenuOpen(false); navigate('/profile/reviews'); }}>Reviews Left</button>
                <button className="block w-full text-left px-4 py-2 hover:bg-green-50 text-gray-700" onClick={() => { setDropdownOpen(false); setMenuOpen(false); navigate('/profile/liked'); }}>Products Liked</button>
                <button className="block w-full text-left px-4 py-2 hover:bg-green-100 text-red-600 font-semibold border-t" onClick={() => { setDropdownOpen(false); onLogout(); }}>Logout</button>
              </div>
            )}
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
